package spiersad;import java.io.File;import java.io.FileNotFoundException;import java.util.Scanner;/** * Created with IntelliJ IDEA. * User: spiers * Date: 5/22/13 * Time: 6:58 PM * To change this template use File | Settings | File Templates. */public class Main {    public final static int studentArraySize = 7;    public static void main(String[] args) {        Scanner lineScanner = readFile("/home/spiers/IdeaProjects/question24/StudentList", "\n");        Student students[] = setStudents(lineScanner);        for (int i = 0; i < studentArraySize; i++){            System.out.println(students[i].toString());        }        System.out.println();        for (int i = 0; i < studentArraySize; i++){            for (int j = 0; j < studentArraySize; j++){                if (students[i].equals(students[j]) && !students[i].getName().equals(students[j].getName()))                    System.out.println(students[i].getName() + " is scored the same as " + students[j].getName());                else if (students[i].compareTo(students[j]) > 0)                    System.out.println(students[i].getName() + " scored better than " + students[j].getName());                else if (students[i].compareTo(students[j]) < 0)                    System.out.println(students[i].getName() + " scored worse than " + students[j].getName());            }        }        lineScanner.close();    }    public static Scanner readFile(String fileName, String delimiter) {        Scanner scanner = null;        try {            File file = new File(fileName);            Scanner scannerScan = new Scanner(file);            scannerScan.useDelimiter(delimiter);            scanner = scannerScan;        }        catch (FileNotFoundException e) {            e.printStackTrace();        }        return scanner;    }    public static Student[] setStudents(Scanner scanner){        Student student[] = new Student[studentArraySize];        for (int i = 0; i < studentArraySize; i++){            student[i] = new Student();        }        for (int i = 0; scanner.hasNext() && i < studentArraySize; i++){            String line = scanner.next();            String[] parts = line.split(",");            try {                student[i].setName(parts[0]);                student[i].setID(Integer.parseInt(parts[1].trim()));                for (int j = 2; j < parts.length; j++){                    student[i].setScores(j-2, Integer.parseInt(parts[j].trim()));                }            }            catch (Exception E){                System.err.println("Student list not formatted correctly");                E.printStackTrace();            }        }        return student;    }}